~~~cpp
/* 



TreeNode* rootbool 
*/  

#include <iostream>  
#include <cstdlib>  
#include <vector>  
#include <queue>  

using namespace std;  

/**/  
struct TreeNode {  
    int val;  
    struct TreeNode *left;  
    struct TreeNode *right;  
    TreeNode(int x) :  
        val(x), left(NULL), right(NULL) {  
    }  
};  

const int flag = INT_MAX;  
TreeNode *generateTree(vector<int> &nums)  
{  
    if (nums.empty())  
        return NULL;  

    TreeNode *root = new TreeNode(nums[0]);  
    queue<TreeNode *> que;  
    que.push(root);  
    //  
    int size = nums.size();  
    for (int i = 1; i < size; i += 2)  
    {  
        //  
        TreeNode *tmp = que.front();  
        TreeNode *left = NULL, *right = NULL;  
        //  
        if (nums[i] != flag)  
        {  
            left = new TreeNode(nums[i]);  
            que.push(left);  
        }  

        //  
        if (i + 1 < size && nums[i + 1] != flag)  
        {  
            right = new TreeNode(nums[i + 1]);  
            que.push(right);  
        }  

        tmp->left = left;  
        tmp->right = right;  
        //  
        que.pop();  
    }  
    return root;  
}  

class Checker {  
public:  

    /* T(n)=O(n) S(n)=O(n)*/  
    void inOrder(TreeNode *root,vector<int> &v)  
    {  
        if (root == NULL)  
            return;  
        inOrder(root->left, v);  
        v.push_back(root->val);  
        inOrder(root->right, v);  
    }  

    bool checkBST1(TreeNode* root)  
    {  
        vector<int> ret;  
        inOrder(root, ret);  
        for (auto i = ret.begin()+1; i != ret.end(); ++i)  
        {  
            if (*i < *(i - 1))  
                return false;  
        }  
        return true;  
    }  

    /**/  
    int lastVal = INT_MIN;  
    bool checkBST2(TreeNode* root) {  
        // write code here  
        if (!root)  
            return true;  

        /**/  
        if (!checkBST2(root->left))  
            return false;  

        /**/  
        if (root->val <= lastVal)  
            return false;  
        lastVal = root->val;  

        /**/  
        if (!checkBST2(root->right))  
            return false;  
        return true;  
    }  

    /**/  
    bool checkBST3(TreeNode* root) {  
        // write code here  
        if (!root)  
            return true;  
        return checkBST3(root, INT_MAX, INT_MIN);  
    }  
    bool checkBST3(TreeNode *root, int maxVal, int minVal)  
    {  
        if (!root)  
            return true;  
        if (root->val < minVal || root->val >= maxVal)  
            return false;  
        if (!checkBST3(root->left, root->val, minVal) || !checkBST3(root->right, maxVal, root->val))  
            return false;  
        return true;  
    }  
};  

int main()  
{  
    vector<int> v = { 7, 6, flag, 4, flag, 2, 5, 8, 3, flag, flag, flag, flag, flag, flag };  
    TreeNode *root = generateTree(v);  

    Checker c;  
    bool ret = c.checkBST1(root);  

    cout << ret << endl;  

    ret = c.checkBST2(root);  

    cout << ret << endl;  

    ret = c.checkBST3(root);  

    cout << ret << endl;  

    system("pause");  
    return 0;  
}  
~~~

