class Solution {
public:
	void replaceSpace(char *str,int length) {
		char *t = str;
        int strLen = 0, newStrLen = 0, spaceCount = 0;
        int p,q;

        if(str == NULL || length <0) return; // 

        while(*t != '\0'){
            strLen++;
            if(*t == ' ') spaceCount++;
            t++;
        }	//  

        newStrLen = strLen + 2 * spaceCount;  // 
        if(newStrLen > length) return ;

        for(p = strLen, q = newStrLen; p >= 0; p-- ){
            if(str[p] == ' '){
                str[q--] = '0';
                str[q--] = '2';
                str[q--] = '%';
            } else {
                str[q--] = str[p];
            }
        }
	}
};
