public boolean addAll(int index, Collection<? extends E> c) {
        //1:indexsize
        checkPositionIndex(index);

        //2:toArray()
        Object[] a = c.toArray();
        int numNew = a.length;
        if (numNew == 0)
            return false;

        //3
        Node<E> pred, succ;
        //lastnull
        if (index == size) {
            succ = null;
            pred = last;
        }
        //node()
        else {
            succ = node(index);
            pred = succ.prev;
        }

        // 4
        for (Object o : a) {
            @SuppressWarnings("unchecked") E e = (E) o;
            //
            Node<E> newNode = new Node<>(pred, e, null);
            //
            if (pred == null)
                first = newNode;
            else
                pred.next = newNode;
            pred = newNode;
        }

        //last
        if (succ == null) {
            last = pred;
        }
        //
        else {
            pred.next = succ;
            succ.prev = pred;
        }

        size += numNew;
        modCount++;
        return true;
    }    
